import React, { Component } from 'react'
import { connect } from "react-redux"
import {i18nContext} from 'contexts/i18n';
import { removeToCart } from 'state/actions'

class Actions extends Component {
  static contextType = i18nContext;
  removeToCart = () => {
    const { prod, index } = this.props

    var txt;
    var r = confirm("Press a button!");
    if (r == true) {
      txt = "You pressed OK!";
    } else {
      txt = "You pressed Cancel!";
    }

    this.props.dispatch( removeToCart(prod,index) )
  }
  render(){
    //let i18n = this.context
    //const { prod, className } = this.props
    const { className } = this.props
    let className_f = className ? className : ""
    return (
      <React.Fragment>
        <h3 onClick={this.removeToCart} className={"cursor-pointer text-dark "+className_f}> &times; </h3>
      </React.Fragment>
    )
  }
}

Actions.contextType = i18nContext
const mapStateToProps = (state) => {
  const { cart } = state
  return { cart }
}
export default connect(mapStateToProps)( Actions );
