import project from './project'

const initialState = {
  project: {
    loading: true,
    name: "Projectree",
    links: [{
        name: 'Site',
        link: 'www.projectree.cloud',
        description: 'My site!'
    }],
    customer: {
      name: 'Myself SPA',
      contacts: [{
        name: 'Mario Rossi',
        email: 'mariorossi@myselfspa.com',
        phone: '55512345',
        role: 'Account',
        note: 'Lorem ipsum',
        alternative_contact: 'Luigi Bianchi', // name of another customer's contact if is not avaiable
      }],
    },
    team: [{
      name: 'John Doe',
      email: 'johndoe@projectree.cloud',
      phone: '55523456',
      role: 'Developer',
      note: 'Nothing to say here',
      leader: 1, // boolean, required. Only one leader for each project
      supervisor: '', // if leader is False, must set the supervisor's name. Default: Team Leader.
    }],
    documentation: [{
      folder: {
        name: '',
        content: [{
          document: {
            name: '',
            content: '', // text saved in db, alternative to "link". Example: README file
            link: '', // link to the file, alternative to "content". Example: PFD of a presentation
          }
        }]
      }
    }],
    accesses: [{
      name: 'Test ENV',
      link: 'dev.projectree.cloud',
      user: 'user',
      password: 'password',
      note: 'Lorem ipsum',
      reading_permissions: '', // Team Only, Team and Customer, List of specific name
    }],
    business_goals: { // the true heart of the project!
      mood: 'Happy', // say in a word if the project has problems. (Delay? The customer is unhappy=? etc..)
    }
  },
}

export default function app(state = initialState, action) {
  return {
    project: project(state.project, action),
  }
}
