export default function cart(state, action) {
    switch (action.type) {
        case 'SET_CART':
            return {
                ...state,
                list:  action.payload.list,
            }
        case 'UPDATE_CART':

        console.log( action.payload.list.id );

            // https://stackoverflow.com/questions/41364383/remove-duplicates-form-an-array/41364495
            return {
                ...state,
                list : state.list ? [...new Set([...state.list, action.payload.list].map(a => a))] : [action.payload.list]
            }
        default:
            return state;
    }
}
