import React from 'react'
//import ReactHtmlParser from 'react-html-parser';
//import ProdLink from "pages/Prods/Prod/parts/Link"
import Schema from "pages/Prods/Prod/parts/Schema"
import { Price } from "components/Prices"
import ProdImage from 'pages/Prods/Prod/parts/Image/index.js'
import { f_cur } from "helpers/formatter"

const Row = (props) => {
    const { prod, className } = props
    let className_f = className ? className : ''
    return (
      <Schema prod={prod} className={"c-row row "+className_f}>
        <div className="row" key={prod.id}>
          <div className="col-3 pr-0 pb-1 ">
            <ProdImage prod={prod} size={'thumb'} useBkg={true} linkStyle={"c-image--bordered"} useLink={true} />
          </div>
          <div className="col">
            <p><strong>{prod.name}</strong> <br /> <Price prod={prod} /></p>
          </div>
        </div>
      </Schema>
    )
}

export default Row

