import React from 'react'
import './style.scss';
import Link from "components/Link/";

const Banner = (props) => {
  const { id, title, cta, link, align, children, img } = props
  const titleBox = title && <h1 className="display-4 mt-4">{title}</h1>
  const ctaBox = cta && <Link className="btn btn-lg mt-4" to={link}>{cta}</Link>
  const childBox = children && <div className="mt-4">{children}</div>
  const imgUrl = process.env.REACT_APP_IMGS+"/banner/"+img+"/desktop.jpg"
  return (
    <div className={"c-banner jumbotron jumbotron-fluid w-100 full-height py-4 text-"+align} id={id}
    style={{backgroundImage:"url("+imgUrl+")"}}
    >
      <div className="container">
        {titleBox}
        {childBox}
        {ctaBox}
      </div>
    </div>
  )
}

export default Banner
