import React, { Component } from 'react'
import { connect } from "react-redux"
import Icon from 'components/Icon/'
import Item from 'pages/Prods/Lists/MiniRow'
import Link from "components/Link/";
import { getPath } from "helpers/getPath"
import {i18nContext} from 'contexts/i18n';

class Cart extends Component {
  toggleDropdown = () => { this.props.statusCart('toggle') }
  closeDropdown = () => { this.props.statusCart('close') }
  static contextType = i18nContext;
  render() {
    let i18n = this.context;
    let path = getPath(i18n.lang)
    const { showCart, cart } = this.props
    const prodotti = cart.list && cart.list.map((prod) => (<Item prod={prod} key={prod.id}>x</Item>))
    const btnSubmit = <div className="row">
                        <div className="col">
                          <Link to={path.cart}
                                onClick={this.closeDropdown}
                                className="btn btn-block btn-success">
                           {i18n.go_to_cart}
                          </Link>
                        </div>
                      </div>
    const fullCart = <React.Fragment>{prodotti} {btnSubmit}</React.Fragment>
    const emptyCart = <React.Fragment>{i18n.empty_cart}</React.Fragment>
    return (
      <div class="nav-item dropdown">
        <div className="text-light mr-3 cursor-pointer" onClick={this.toggleDropdown} >
          <Icon icon={'icon-shopping-cart'} text={ (cart.list && cart.list.length > 0) && <span className="badge badge-success">{cart.list.length}</span>}/>
        </div>
        <div class={"dropdown-menu p-3 "+( showCart ? 'show' : '' )} role="menu">
          {cart.list ? fullCart : emptyCart}
        </div>
      </div>
    )
  }
}
Cart.contextType = i18nContext
const mapStateToProps = (state) => {
  const { cart } = state
  return { cart }
}
export default connect(mapStateToProps)( Cart );
