import './style.scss';
import React, { Component } from 'react'
import { connect } from "react-redux"
import Link from "components/Link/";
import Nav from './Nav'
import Cart from './Cart/'
import { getPath } from "helpers/getPath"
import {i18nContext} from 'contexts/i18n'
import Icon from 'components/Icon/'

class Header extends Component {
  constructor(props) {
    super(props)
    this.state = {
      showNavigation: false,
      showCart: true
    }
  }
  toggleNavigation = () => { this.state.showNavigation ? this.statusNavigation('hide') : this.statusNavigation('show') }
  statusNavigation = (status) => { this.setState({ showNavigation: ( status==='show' ? true : false ) }) }
  toggleCart = () => { this.state.showCart ? this.statusCart('hide') : this.statusCart('show') }
  statusCart = (status) => { this.setState({ showCart: ( status==='show' ? true : false ) }) }
  static contextType = i18nContext;
  render() {
    const { REACT_APP_NAME } = process.env
    const { cart } = this.props
    let i18n = this.context;
    let path = getPath( i18n.lang )
    return (
      <nav className="t-header navbar sticky-top navbar-expand-lg navbar-dark bg-dark">
        <div className="container px-0">
          {/* LOGO */}
          <Link className="navbar-brand btn btn-link pl-0 pl-md-1 font-weight-normal" to={path.lang}>
            <Icon icon={'icon-fire'} iconStyle={'mr-1'} text={REACT_APP_NAME} />
          </Link>
          <div class="flex-grow-1">
            &nbsp;
          </div>
          {/* CART */}
          <div className="text-light mr-3"
               onClick={this.toggleCart}
              >
            <Icon icon={'icon-shopping-cart'} text={<span className="badge badge-success">{cart.list.length}</span>}/>
          </div>
          <div className={"collapse navbar-collapse "+( this.state.showCart ? 'show' : '' )} id="navbarSupportedContent">
            x
          </div>
          {/* NAVIGATION */}
          <div onClick={this.toggleNavigation}
               className="navbar-toggler"
               type="button"
               data-toggle="collapse"
               data-target="#navbarSupportedContent"
               aria-controls="navbarSupportedContent"
               aria-expanded="false"
               aria-label="Toggle navigation"
              >
            <span className="navbar-toggler-icon">
            </span>
          </div>
          <div className={"collapse navbar-collapse "+( this.state.showNavigation ? 'show' : '' )} id="navbarSupportedContent">
            <Nav
              listClass="navbar-nav ml-auto mr-2"
              elemsClass="nav-item"
              linksClass="nav-link"
              closeModalHandler={this.closeModalHandler}
            />
          </div>
        </div>
      </nav>
    )
  }
}
Header.contextType = i18nContext

const mapStateToProps = (state) => {
  const { cart } = state
  return {
    cart
  }
}
export default connect(mapStateToProps)( Header );
