import './style.scss';
import React, { Component } from 'react';
import { connect } from "react-redux";
//import {i18nContext} from 'contexts/i18n';
import Link from "components/Link/";

class Banner extends Component {
  //static contextType = i18nContext;
  render() {
    //let i18n = this.context;
    const { id, title, link, align, children, img, classes } = this.props
    const titleBox = title && <h1 className="display-4">{title}</h1>
    //const ctaBox = <div className="btn btn-lg mt-4" to={link}>{i18n.actions.go_to_prods}</div>
    const childBox = children && <div className="mt-4">{children}</div>
    const imgUrl = process.env.REACT_APP_IMGS+"/categories/"+img+".jpg"

    const c = "c-banner c-banner--with-img d-flex align-items-center jumbotron jumbotron-fluid py-4 w-100 text-"+align+' '+classes

    const content = <React.Fragment>
                      <div className="container">
                        {titleBox}
                        {childBox}
                      </div>
                      <div className='c-banner__bkg img-bkg'
                           style={{backgroundImage:"url("+imgUrl+")"}} >
                      </div>
                    </React.Fragment>

    const type = link ? <Link to={link} id={id} className={c+' c-banner--with-link'} > {content} </Link> : <div id={id} className={c}> {content} </div>

    return (
      <React.Fragment>{type}</React.Fragment>
    )
  }
}
//Banner.contextType = i18nContext
const mapStateToProps = (state) => {
  const { categories } = state
  return {
    loading: categories.loading,
    categories: categories.list
  }
}
export default connect(mapStateToProps)( (Banner) );
