import React, { Component } from 'react'
import { connect } from "react-redux";

class Footer extends Component {
  render() {
    const { sitename } = this.props
    const year = new Date().getFullYear();
    return (
      <footer className="t-footer">
        <div className="text-center my-2">
          {year} &copy; {sitename}
          <span className="px-2">|</span>
          <a href="https://www.offerprive.it/forms/info.php?pag=privacy&amp;lang=it" target="_blank" rel="noopener noreferrer">Privacy</a>
          <span className="px-2">|</span>
          <a href="https://www.offerprive.it/forms/info.php?pag=terms&amp;lang=it" target="_blank" rel="noopener noreferrer">Terms</a>
        </div>
      </footer>
    )
  }
}

const mapStateToProps = (state) => {
  const { sitename } = state
  return {
    sitename
  }
}

export default connect(mapStateToProps)(Footer);
