import React, { Component } from 'react'
import Related from './Item'
import {i18nContext} from 'contexts/i18n';

export default class Relateds extends Component {
    static contextType = i18nContext;
    render(){
    let i18n = this.context;
    const { prod, openModalHandler, title } = this.props
    const Relateds = (prod && prod.relateds) &&
                    prod.relateds.map((p, i) => (
                        <Related prod={p}
                                 key={i}
                                 openModalHandler={openModalHandler} />
                    ));
    const currTitle = title ? title : i18n.product.related
    return (
      <React.Fragment>
        {(prod && prod.relateds) ?
          <section className="c-related__wrapper">
            <h3 className="text-center pt-2 pb-4">
              {currTitle}
            </h3>
            <div className="row related__wrapper d-flex justify-content-center">
              {Relateds}
            </div>
          </section>
        : '' }
      </React.Fragment>
    )
  }
}
Relateds.contextType = i18nContext
