import './style.scss';
import React, { Component } from 'react'
import { connect } from "react-redux"
import Link from "components/Link/";
import Nav from './Nav'
import { getPath } from "helpers/getPath"
import {i18nContext} from 'contexts/i18n'
import Icon from 'components/Icon/'

import { fetchCart } from 'state/actions'
import { parseIfJsonString } from "helpers/object_and_json"
import { getCookie, setCookie } from "helpers/cookies"


class Header extends Component {
  constructor(props) {
    super(props)
    this.state = {
      isShowing: false
    }
  }
  toggleModalHandler = () => {
    this.state.isShowing ? this.closeModalHandler() : this.showModalHandler()
  }
  showModalHandler = () => {
    this.setState({ isShowing: true });
  }
  closeModalHandler = () => {
    this.setState({ isShowing: false });
  }
  static contextType = i18nContext;
  render() {
    const { REACT_APP_NAME } = process.env
    const { cart } = this.props
    let i18n = this.context;
    let path = getPath( i18n.lang )
    //setCookie('cart',cart) // inizializza il cart nel cookie

    //console.log( getCookie('cart') )


    //
    let currCart = parseIfJsonString( getCookie('cart') )

    cartCompare = JSON.stringify(cart.list)
    currCartCompare = JSON.stringify(currCart.list)
    console.log('currCart',cartCompare,'cookieCart',currCartCompare);

    if(cartCompare!==currCartCompare) this.props.dispatch( fetchCart(currCart.list) )


    // console.log( typeof currCart, currCart )
    // if( currCart.list ) currCart.list= cart
    // console.log( currCart )

    //setCookie('cart', currCart.list )




    return (
      <nav className="t-header navbar sticky-top navbar-expand-lg navbar-dark bg-dark">
        <div className="container px-0">
          <Link className="navbar-brand btn btn-link pl-0 pl-md-1 font-weight-normal" to={path.lang}>
            <Icon icon={'icon-fire'} iconStyle={'mr-1'} text={REACT_APP_NAME} />
          </Link>
          <div
                onClick={this.toggleModalHandler}
                className="navbar-toggler"
                type="button"
                data-toggle="collapse"
                data-target="#navbarSupportedContent"
                aria-controls="navbarSupportedContent"
                aria-expanded="false"
                aria-label="Toggle navigation">
            <span className="navbar-toggler-icon">
            </span>
          </div>
          <div className={"collapse navbar-collapse "+( this.state.isShowing ? 'show' : '' )} id="navbarSupportedContent">
            <Nav
              listClass="navbar-nav ml-auto mr-2"
              elemsClass="nav-item"
              linksClass="nav-link"
              closeModalHandler={this.closeModalHandler}
            />
          </div>
          <div className="text-light" onClick={this.addToCart}>
            <Icon icon={'icon-shopping-cart'} text={<span className="badge badge-success">{cart.list.length}</span>}/>
          </div>
        </div>
      </nav>
    )
  }
}
Header.contextType = i18nContext

const mapStateToProps = (state) => {
  const { cart } = state
  return {
    cart
  }
}
export default connect(mapStateToProps)( Header );
