import React, { Component } from 'react'
import {i18nContext} from 'contexts/i18n';
import { getPath } from "helpers/getPath"
import withTemplate from 'templates/'
import { Full } from 'pages/Prods/Lists/Row'
import Form from 'pages/Cart/form'
import Link from "components/Link/";
import Modal from 'components/Modal/';
import { removeToCart } from 'state/actions'

class Cart extends Component {
  static contextType = i18nContext;
  constructor(props) {
    super(props)
    this.state = {
      isShowingModal: false,
      isShowingThanks: false,
    }
  }
  openModal = () => { this.setState({ isShowingModal: true }) }
  closeModal = () => { this.setState({ isShowingModal: false }) }
  sayThanks = () => { this.setState({ isShowingThanks: true }) }
  render(){
    let path = getPath()
    let i18n = this.context;
    const { cart } = this.props
    const cartIsFull = (cart && !cart.submitted && cart.list && cart.list.length > 0 )
    return (
      <div className="p-cart">
        { cartIsFull
          ? <CartFull sayThanks={this.sayThanks} enableModal={true} cart={cart} openModal={this.openModal} closeModal={this.closeModal} isShowingModal={this.state.isShowingModal} i18n={i18n} />
          : <CartEmpty thanks={this.state.isShowingThanks} i18n={i18n} path={path} /> }
      </div>
    )
  }
}
Cart.contextType = i18nContext
export default withTemplate(Cart)

// - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -
// PRIVATE

const CartEmpty = (props) => {
  const { i18n, path, thanks } = props
  return (
    <div className="text-center">
      { thanks
        ? <div>
            <h3>{i18n.thanks_after_order}</h3>
            <p>{i18n.alert_more_than_one_call}</p>
          </div>
        : <div>
            <p>{i18n.empty_cart}</p>
          </div>
      }
      <p><Link to={path.home} className="btn btn-success">{i18n.back_to_home}</Link></p>
    </div>
  )
}

const CartFull = (props) => {
  const { enableModal, cart, openModal, closeModal, isShowingModal, i18n, sayThanks } = props
  return (
    <div className="row">
      <div className="col-12">
        <h2>{i18n.cart}</h2> <hr />
      </div>
      <div className="col-12 pb-3">
        <ProdsList cart={cart} />
      </div>
      <div className="col-12 pb-3 text-center">
        { !enableModal
          ? <FormUser onSubmit={sayThanks} />
          : <ModalUser open={openModal} close={closeModal} sayThanks={sayThanks} isShowingModal={isShowingModal} i18n={i18n} /> }
      </div>
      <div className="col-12 pt-3">
        <div className="alert alert-secondary">
          {i18n.alert_more_than_one_call}
        </div>
      </div>
    </div>
  )
}

const ProdsList = (props) => {
  const { cart } = props
  const Item = Full
  return (
    cart.list.map((prod,index) => (
      <Item prod={prod} key={index}>
        <RemoveBtn prod={prod} key={index} index={index} />
      </Item>
    ))
  )
}

class RemoveBtn extends Component {
  removeToCart = () => {
    const { prod, index } = this.props
    const i18n = this.context
    // Finestra di conferma. Se si clicca ok, tornerÃ  TRUE.
    if ( window.confirm(i18n.confirm_delete) === true ) {

      console.log( removeToCart() )
      this.props.dispatch( removeToCart(prod,index) )
    }
  }
  render() {
    const { className } = this.props
    let className_f = className ? className : ""
    return (
      <h3 onClick={this.removeToCart}
          className={"p-cart__action cursor-pointer text-dark "+className_f}>
        &times;
      </h3>
    )
  }
}

class ModalUser extends Component {
  onCloseModal = () => {
    const { close, sayThanks } = this.props
    close()
    sayThanks()
  }
  render(){
    const { isShowingModal, open, close, i18n } = this.props
    return (
      <React.Fragment>
        <Modal show={isShowingModal}
               close={close}
               title={i18n.order_now_cta}>
            {isShowingModal && <FormUser onSubmit={this.onCloseModal} /> }
        </Modal>
        <button className="btn btn-success btn-lg d-inline-block"
                onClick={open} >
          {i18n.order_now_cta}
        </button>
      </React.Fragment>
    )
  }
}

const FormUser = (props) => {
  return ( <Form onSubmit={props.onSubmit} /> )
}
