import './style.scss';
import React, { Component } from 'react'
import { connect } from "react-redux";
import { getCookie } from "helpers/cookies"
import { parseIfJsonString } from "helpers/object_and_json"
import {i18nContext} from 'contexts/i18n';

class Form extends Component {
  static contextType = i18nContext;
  render() {
    let i18n = this.context;
    const { prod, tracker } = this.props
    let linkIframe = iframeFormLink(prod,tracker,i18n)
    const heightIframe = '300px';
    return (
      <div className="c-form">
        <div className="frm-call-api"></div>
        <iframe title="form-order"
                src={linkIframe}
                frameBorder="0"
                width="100%" height={heightIframe}
                style={{ height: heightIframe }}
                className="c-form__iframe">
        </iframe>
      </div>
    )
  }
}
Form.contextType = i18nContext
const mapStateToProps = (state) => {
  const {tracker} = state
  return { tracker }
}
export default connect(mapStateToProps)(Form);

// - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -
// VARIABILI PRIVATE (non esportate)

const iframeFormLink = (prod,tracker,i18n)=>{
  let resultLink, startLink, trackLink, formParams
  const { trackname, trackid, lanname, lanid } = tracker
  // inizio del link (path delle api + pagina alla form del network corretto)
  startLink = process.env.REACT_APP_FORM+"?network=form_" + prod.network
  // se esiste il traker, lo aggiunge all'url nella querystring
  trackLink = ( trackname && trackid ) ? "&" + trackname + "=" + trackid : ''
  // aggiunge i parametri principali
  formParams = "&network=" + prod.network
  formParams += "&lang=" + prod.lang
  formParams += "&product=" + prod.code
  // se esiste lan, lo aggiunge come subid all'url nel campo dei parametri
  formParams += ( lanname && lanid ) ? ("&" + lanname + "=" + lanid) : ''
  // aggiunge la grafica:
  // creo una stringa con tutti i parametri settati in dinamicDesignParams()
  let designParamsList = dinamicDesignParams(prod.id,i18n)
  Object.keys(designParamsList).map((p, i) => {
    formParams += '&'+p+'='+designParamsList[p]
    return true
  })
  // faccio encoding della query dei caratteri trovati perchè
  // risultino tutti una stringa unica, valore di "&params"
  formParams = encodeURIComponent(formParams)
  // monta il link
  resultLink = startLink + trackLink + "&params=" + formParams
  return resultLink
}

const dinamicDesignParams = (id,i18n)=>{

  // TODO: trovare il modo di validare i dati che arrivano dal cookie
  //let zipcode = "22331133".substring(0,5)

  let defaultParams = {
    bodypadding:"0px",
    fontsize:"16px",
    btnsize:"18px",
    marginvert:"1",
    btnbkg:"#28a745",
    btncolor:"#ffffff",
    btnradius:"3px",
    btnalign:"center",
    inputborder:"1px solid rgba(0,0,0,.125)",
    inputheight:"2em",
    inputpadding:"1em 0.5em",
    hidelabel:"1",
    footerhide:"1",
    btnlabel: i18n.proceed,
    tnkstolink:"/it/relateds/"+id+"#/",
    //info utente
    // name: "Ciccio",
    // phone: "123123123123",
    // address: "123123123123",
    // city: "123123123123",
    // zipcode,
    // email: "Ciccio",
  }

  console.log( 'tnkstolink', defaultParams['tnkstolink'] );

  // ora che ho creato i parametri  di default,
  // faccio replace coi parametri nel cookie abTest
  const abTest = parseIfJsonString( getCookie('abTest') );
  Object.keys( abTest ).map((p, i) => {
    //console.log('p',p,defaultParams[p],abTest[p]);
    if( abTest[p] ) defaultParams[p] = abTest[p]
    return true
  })
  // !!!! QUI STAMPARE IL NUOVO OGGETTO
  return defaultParams;
}
