import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import { getPath } from "helpers/getPath"
import {i18nContext} from 'contexts/i18n';

export default class ProdEmpty extends Component {
  static contextType = i18nContext;
  render(){
  let i18n = this.context;
  let path = getPath(i18n.lang)
  return (
      <div className="p-prod__empty text-center">
        <p>{i18n.prod_not_available}</p>
        <Link className='btn btn-primary' to={path.prods}>
          {i18n.cta_back_to_list}
        </Link>
      </div>
      )
    }
}

ProdEmpty.contextType = i18nContext
