import React, { Component } from 'react'
import { connect } from "react-redux"
import Icon from 'components/Icon/'
import ProdImage from 'pages/Prods/Prod/parts/Image/index.js'

class Header extends Component {
  toggleDropdown = () => { this.props.statusCart('toggle') }
  render() {
  let i18n = this.context;
    const { showCart, cart } = this.props
    const prodotti = cart.list && cart.list.map((prod) => (
      <div className="row" key={prod.id}>
        <div className="col-4">
          <ProdImage prod={prod} size={'thumb'} useBkg={true} imgStyle={"c-image--bordered"} useLink={true} />
        </div>
        <div className="col">
          {prod.name}
        </div>
      </div>))
      const content = cart.list ? prodotti : <div>{i18n.empty_cart}</div>
    return (
      <div class="nav-item dropdown">
        <div className="text-light mr-3" onClick={this.toggleDropdown} >
          <Icon icon={'icon-shopping-cart'} text={ (cart.list && cart.list.length > 0) && <span className="badge badge-success">{cart.list.length}</span>}/>
        </div>
        <div class={"dropdown-menu p-3 "+( showCart ? 'show' : '' )} role="menu"> {content} </div>
      </div>
    )
  }
}
const mapStateToProps = (state) => {
  const { cart } = state
  return { cart }
}
export default connect(mapStateToProps)( Header );
