import './style.scss';
import React, { Component } from 'react';
import { connect } from "react-redux";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import Home from 'pages/Home'
//import About from 'pages/About'
import Cart from 'pages/Cart'
import Prods from 'pages/Prods'
import Prod from 'pages/Prods/Prod/'
import Relateds from 'pages/Prods/Relateds/'
import Page404 from 'pages/Page404'
import { getPath } from "helpers/getPath"

// Context: importo sia contesto che oggetto
import { i18nContext, i18nObj } from 'contexts/i18n';

class App extends Component {
  // Creo lo stato di App
  constructor(props) {
    super(props);
    // recupero la lang di default dallo stato globale
    let globalStateLang = i18nObj[props.lang]
    // setto la lang di default nello stato interno
    this.state = { i18nValues: globalStateLang };
    // NON CANCELLARE, TENERE COME TEST: Esempio di toggle dello stato interno
    this.toggleI18N = (lang)=>{
      if( i18nObj[lang] ) {
        this.setState(
          state=>({
            i18nValues: i18nObj[lang]
            })
        )
      }
    };
  }
  render() {
    // Lingua dello stato locale di App, settato con lo stato globale/del browser
    // imposta i path con la lingua del browser
    let path = getPath()
    return (
      <Router>
        <div className="">
          <Switch>
            <Route exact path={path.home} render={ props => <Home {...props} /> } />
          </Switch>
        </div>
      </Router>
    )
  }
}

const mapStateToProps = (state) => {
  return {
  }
}
export default connect(mapStateToProps)(App);
