import React, { Component } from 'react';
import { connect } from "react-redux";
import { getVisibleProds } from 'state/selectors'
import Item from 'pages/Prods/Lists/Row/'
import {i18nContext} from 'contexts/i18n';
import { setLang, fetchProds } from 'state/actions'
import Loading from 'pages/Loading'
import withTemplate from 'templates/'
// import withTemplate from 'templates/'

class Prods extends Component {
  static contextType = i18nContext;
  componentDidMount () {
    this.updateAll( this.props.match.params.lang )
  }
  updateAll = (lang) => {
    this.props.dispatch(fetchProds(lang))
    this.props.dispatch(setLang(lang))
  }
  render() {
    let i18n = this.context;
    const { prods, loading } = this.props
    if (loading) { return ( <Loading /> ) }
    const prodsFull = prods.map((prod) => ( <Item prod={prod} key={prod.id} /> ))
    const prodsEmpty = <span> {i18n.products_no_results} </span>
    return (
      <div className="p-prods">
        <section className="c-prods__wrapper">
          { prods && prods.length > 0  ? prodsFull : prodsEmpty }
        </section>
      </div>
    )
  }
}
Prods.contextType = i18nContext
const mapStateToProps = (state) => {
  const { currency, prods } = state
  return {
    prods: getVisibleProds(state),
    currency,
    loading: prods.loading
  }
}
export default connect(mapStateToProps)( withTemplate(Prods) );
