import './style.scss';
import React, { Component } from 'react'
import { connect } from "react-redux"
import Link from "components/Link/";
import Nav from './Nav'
import Cart from './Cart/'
import Navigation from './Navigation/'
import { getPath } from "helpers/getPath"
import {i18nContext} from 'contexts/i18n'
import Icon from 'components/Icon/'

class Header extends Component {
  constructor(props) {
    super(props)
    this.state = {
      showNavigation: false
    }
  }
  toggleNavigation = () => { this.state.showNavigation ? this.statusNavigation('hide') : this.statusNavigation('show') }
  statusNavigation = (status) => { this.setState({ showNavigation: ( status==='show' ? true : false ) }) }
  static contextType = i18nContext;
  render() {
    let i18n = this.context;
    let path = getPath( i18n.lang )
    return (
      <React.Fragment>
        <div onClick={this.toggleNavigation}
             className="navbar-toggler"
             type="button"
             data-toggle="collapse"
             data-target="#navbarSupportedContent"
             aria-controls="navbarSupportedContent"
             aria-expanded="false"
             aria-label="Toggle navigation"
            >
          <span className="navbar-toggler-icon">
          </span>
        </div>
        <div className={"collapse navbar-collapse "+( this.state.showNavigation ? 'show' : '' )} id="navbarSupportedContent">
          <Nav
            listClass="navbar-nav ml-auto mr-2"
            elemsClass="nav-item"
            linksClass="nav-link"
            closeModalHandler={this.closeModalHandler}
          />
        </div>
      </React.Fragment>
    )
  }
}
Header.contextType = i18nContext

const mapStateToProps = (state) => {
  const { cart } = state
  return {
    cart
  }
}
export default connect(mapStateToProps)( Header );
