export default function cart(state, action) {
    switch (action.type) {
        case 'SET_CART':
            return {
                ...state,
                list:  action.payload.list,
            }
        case 'UPDATE_CART':
          let p = action.payload.list
          let prod = { id:p.id, network:p.network, code:p.code, name:p.name, slug:p.slug,
                       lang:p.lang,country:p.country, price:p.price, oldprice:p.oldprice }
          // https://stackoverflow.com/questions/41364383/remove-duplicates-form-an-array/41364495
          return {
              ...state,
              list : state.list ? [...new Set([...state.list, prod].map(a => a))] : [prod]
          }
        default:
            return state;
    }
}
