import './style.scss';
import React, { Component } from 'react'
import { connect } from "react-redux"
import Link from "components/Link/";
import Navigation from './Navigation'
import Cart from './Cart/'
import { getPath } from "helpers/getPath"
import {i18nContext} from 'contexts/i18n'
import Icon from 'components/Icon/'

class Header extends Component {
  constructor(props) {
    super(props)
    this.state = {
      showNavigation: false,
      showCart: false
    }
  }
  toggleNavigation = () => {
    this.state.showNavigation ? this.statusNavigation('hide') : this.statusNavigation('show')
  }
  toggleCart = () => {
    this.state.showCart ? this.statusCart('hide') : this.statusCart('show')
  }
  statusNavigation = (status) => {
    let finalStatus = status==='show' ? true : status === 'hide' ? false : !!this.state.showCart
    this.setState({ showNavigation: finalStatus })
    this.setState({ showCart: false })
  }
  statusCart = (status) => {
    let finalStatus = status==='show' ? true : status === 'hide' ? false : !!this.state.showCart
    this.setState({ showCart: finalStatus })
    this.setState({ showNavigation: false })
  }
  static contextType = i18nContext;
  render() {
    const { REACT_APP_NAME } = process.env
    const { cart } = this.props
    let i18n = this.context;
    let path = getPath( i18n.lang )
    return (
      <nav className="t-header navbar sticky-top navbar-expand-lg navbar-dark bg-dark">
        <div className="container px-0">
          {/* LOGO */}
          <Link className="navbar-brand btn btn-link pl-0 pl-md-1 font-weight-normal" to={path.lang}>
            <Icon icon={'icon-fire'} iconStyle={'mr-1'} text={REACT_APP_NAME} />
          </Link>
          <div class="flex-grow-1">
            &nbsp;
          </div>
          <Cart toggleCart={this.toggleCart} showCart={this.state.showCart} cart={cart} />
          <Navigation toggleNavigation={this.toggleNavigation} showNavigation={this.state.showNavigation} statusNavigation={this.statusNavigation}  />
        </div>
      </nav>
    )
  }
}
Header.contextType = i18nContext

const mapStateToProps = (state) => {
  const { cart } = state
  return {
    cart
  }
}
export default connect(mapStateToProps)( Header );
