export default function cart(state, action) {

  let a = action.payload
  let p = a ? a.list : false
  let ls = state.list

  let prod = p ? { id:p.id, network:p.network, code:p.code, name:p.name,
                   slug:p.slug, lang:p.lang, country:p.country,
                   currency:p.currency, price:p.price, oldprice:p.oldprice }
               : false

  switch (action.type) {
      case 'SET_CART':
          return {
              ...state,
              list:  a.list,
          }
      case 'ADD_TO_CART':
        // https://stackoverflow.com/questions/41364383/remove-duplicates-form-an-array/41364495

        for (var i = 0; i < ls.length; i++) {
          if( ls[i].id === prod.id ) console.log(ls[i].id,prod.id)
        }

        return state;

        // let prodInList = [...new Set([...ls, prod].map(a => a))]
        // let firstProd = [prod]
        // return {
        //     ...state,
        //     list : ls ? prodInList : firstProd
        // }
      case 'REMOVE_TO_CART':
        if( ls.length > 0 && a.index !== undefined ) {
          let newArray = ls // copio l'array in un'altra cella di memoria
          newArray.splice(a.index,1) // modifico l'array
          return {
              ...state,
              list : newArray
          }
        } else {
          return state;
        }

      default:
          return state;
    }
}
