export default function cart(state, action) {
    switch (action.type) {
        case 'SET_CART':
            return {
                ...state,
                list:  action.payload.list,
            }
        case 'UPDATE_CART':

          //let prod = JSON.stringify(action.payload.list)
          let prod = '"'+action.payload.list.id+'"' //action.payload.list.id

          console.log( typeof JSON.stringify(action.payload.list), JSON.stringify(action.payload.list));

          // https://stackoverflow.com/questions/41364383/remove-duplicates-form-an-array/41364495
          return {
              ...state,
              list : state.list ? [...new Set([...state.list, prod].map(a => a))] : [prod]
          }
        default:
            return state;
    }
}
