import React, { Component } from 'react'
import { connect } from "react-redux";
import {i18nContext} from 'contexts/i18n';
import { indexProdInList } from 'helpers/object_and_json.js'
import Icon from 'components/Icon/'

class AsideProd extends Component {
  static contextType = i18nContext;
  removeFromCart = () => { this.props.removeFromCart(this.props.prod) }
  addToCart = () => { this.props.addToCart() }
  render(){
  let i18n = this.context;
  const { prod } = this.props
  const { openModalHandler, cart } = this.props
  const action = (indexProdInList(prod,cart.list) >= 0)
                    ? { onClick: this.removeFromCart,
                        color: 'text-default',
                        color: 'text-success',
                        icon: 'icon-times',
                        text: i18n.remove_from_cart }
                    : { onClick: this.addToCart,
                        color: 'text-success',
                        icon: 'icon-shopping-cart',
                        text: i18n.add_to_cart }
  return (
    <React.Fragment>
        <button className="mt-3 btn btn-block btn-lg btn-success"
                onClick={openModalHandler}> {i18n.order_now_cta}
        </button>
        <span style={{'maxWidth':'400px'}}
              onClick={action.onClick}
              className={"btn btn-text mx-auto mt-2 d-block "+action.color} >
              <Icon icon={action.icon}
                    text={action.text}
                    iconWrapperStyle='w-100 justify-content-center' />
        </span>
      </React.Fragment>
    )
  }
}
AsideProd.contextType = i18nContext
const mapStateToProps = (state) => {
  const { currency, prods, cart } = state
  return {
    currency,
    loading: prods.loading,
    error: prods.error,
    cart
  }
}
export default connect(mapStateToProps)( AsideProd );
