import React, { Component } from 'react'
import Aside from 'components/Aside'
import PricesBox from 'components/Prices'
import TrustabilityBadges from "components/TrustabilityBadges/"
import {i18nContext} from 'contexts/i18n';
import Form from './Form/'

export default class AsideProd extends Component {
  static contextType = i18nContext;
  render(){
  let i18n = this.context;
  const { prod, showForm, openModalHandler, addToCart } = this.props
  const btn = <React.Fragment>
                <button className="mt-3 btn btn-block btn-lg btn-success"
                         onClick={openModalHandler}> {i18n.order_now_cta}
                </button>
                <span className="btn btn-text mx-auto mt-2"
                        onClick={addToCart}
                        style={{'maxWidth':'400px'}} >
                  {i18n.add_to_cart}
                </span>
              </React.Fragment>
  const form = <div className="pt-4">
                      <Form prod={prod} />
                  </div>
  return (
        <Aside>
            <h4 className="mb-2">{i18n.order_now_cta} {prod.name}</h4>
            <hr />
            <PricesBox prod={prod}
                    colLabelStyle="col-7 text-left "
                    colPriceStyle="col text-right " />
            { showForm ? form : btn }
            <hr />
            <TrustabilityBadges itemStyle={'is-icons-empty'} />
        </Aside>
      )
    }
}
AsideProd.contextType = i18nContext
