import React, { Component } from 'react'
import ReactHtmlParser from 'react-html-parser';
import { Link } from 'react-router-dom'
import Image from 'components/Image'
import { getProdLinkImg } from "helpers/getPath"

export default class Box extends Component {
  render() {
    const { box, prod, openModalHandler, fullWidth } = this.props
    const alignment = ' text-left text-sm-left '
    const spacing = fullWidth ? '' : ' mt-4 mb-4 pb-sm-4 '
    const textCta = ( box.cta !== true ) ? box.cta : 'Ordina subito'
    const imgUrl = getProdLinkImg(prod.lang,prod)+'/box/'+box.img
    return (
      <article className={"row c-box align-items-start "+spacing}>
        {
          (!!box.img) &&
          <div className={"col-12 XXX-pb-2 pb-sm-3 col-sm-4 col-lg-5 pb-sm-0 "+( fullWidth ? "col-lg-3" : '' )}>
            <Image src={imgUrl}
                   alt={prod.slug}
                   imgStyle="img-fluid XXX-mt-2 "
                   useBkg={false}
                   />
          </div>
        }
        <div className={"col "+alignment}>
          <h3 className="mb-3">{ReactHtmlParser(box.title)}</h3>
          <div className="c-box__txt">{ReactHtmlParser(box.text)}</div>
          {
            (!!box.cta) &&
            <Link to="#"
                className="btn btn-secondary text-uppercase"
                onClick={openModalHandler}>
                {textCta}
            </Link>
          }
        </div>
      </article>
    )
  }
}
