import React, { Component } from 'react'
import { connect } from "react-redux"
import Nav from './Nav'
import Link from "components/Link/";
import Icon from 'components/Icon/'

class Header extends Component {
  toggleDropdown = () => { this.props.statusNavigation('toggle') }
  render() {
    const { showMenu, statusMenu } = this.props
    return (
      <React.Fragment>
        <div className="nav-item dropdown">
          <Link className="nav-link dropdown-toggle " to="#" onClick={this.toggleDropdown}>
            <Icon icon={'icon-bars'} iconStyle={( showMenu ? 'is-active' : '' )}/>
          </Link>
          <Nav
            listClass={"dropdown-menu "+( showMenu ? 'show' : '' )}
            elemsClass="nav-item"
            linksClass="nav-link"
            statusNavigation={statusMenu}
          />
        </div>
      </React.Fragment>
    )
  }
}

const mapStateToProps = (state) => {
  const { cart } = state
  return {
    cart
  }
}
export default connect(mapStateToProps)( Header );
