    const fieldName = label ? label : placeholder
                          ? placeholder : form[name]
                          ? form[name] : name;
    return (
      <React.Fragment>
        {!hidelabel && <label className="text-capitalize">{fieldName}</label>}
        <input {...this.props}
               className={"form-control"+f_ifexist(className)}
               type={type?type:"text"}
               placeholder={fieldName}
               id={id?id:name} />
      </React.Fragment>
    )
  }
}
